    public void fetch(){
        try{
         String url = "jdbc:mysql://localhost/record"; 
          Connection conn = DriverManager.getConnection(url,"root",""); 
            //Statement st = conn.createStatement();
         String sql= "select*from personalinfo";
           PreparedStatement pstmt = conn.prepareStatement(sql);
           ResultSet rs=pstmt.executeQuery();
          JTableHeader threader=jTable1.getTableHeader();
          threader.setForeground(Color.PINK);
          threader.setBackground(Color.GREEN);
          jTable1.setModel(DbUtils.resultSetToTableModel(rs));
          threader.getBorder();
          threader.getBounds();
          
        
        }catch(Exception e){
            JOptionPane.showMessageDialog(null,e);
        }
    }
    public ImageIcon ResizeImage(String imagepath,byte[] pic){
        ImageIcon myImage=null;
        if(imagepath!=null){
          myImage=new ImageIcon(imagepath)  ;
        }
        else{
          myImage=new ImageIcon(pic)  ;  
        }
        Image imge=myImage.getImage();
        Image newImage=imge.getScaledInstance(lab.getWidth(), lab.getHeight(),Image.SCALE_DEFAULT);
        ImageIcon image=new ImageIcon(newImage);
        return image;
    }
